import SwiftUI
import Colors

//TODO: this class was made specifically for Home view, but if we're going to use it everywhere, should remove home-specific stuff (isGreeting, profilePic)
public struct TitleSection: View {
	public enum ProfilePic {
		case show(URL?)
		case hide
	}
	
	public let isGreeting: Bool
	public let title: String
	public let profilePic: ProfilePic
	public let gradient: [String]

	public init(isGreeting: Bool = false, title: String, profilePic: ProfilePic = .hide, gradient: [String]) {
		self.isGreeting = isGreeting
		self.title = title
		self.profilePic = profilePic
		self.gradient = gradient
	}
	
    public var body: some View {
		HStack {
			titleView
			Spacer()
			profilePicView
		}
		.addSidePadding()
		.frame(height: 50)
    }
	
	@ViewBuilder
	private var titleView: some View {
		let title = Text(title)
			.font(.newmTitle1)
		if isGreeting {
			title
		} else {
			title.foregroundStyle(LinearGradient(colors: gradient.colors,
												 startPoint: .bottomLeading,
												 endPoint: .topTrailing))
		}
	}
	
	@ViewBuilder
	private var profilePicView: some View {
		switch profilePic {
		case .show(let url):
			if let url = url {
				AsyncImage(url: url) { phase in
					switch phase {
					case .success(let image):
						image.circleImage(size: 44)
					default:
						//TODO: log
						Image.placeholder.circleImage(size: 44)
					}
				}
			} else {
				Image.placeholder.circleImage(size: 44)
			}
		case .hide:
			EmptyView()
		}
	}
}

struct TitleSection_Previews: PreviewProvider {
    static var previews: some View {
		Group {
			TitleSection(isGreeting: false, title: "HOME", profilePic: TitleSection.ProfilePic.show(URL(string: "data:image/jpeg;base64,")!), gradient: ["#aa44ff", "#2349ad"])
			TitleSection(isGreeting: true, title: "YO DAWG", profilePic: TitleSection.ProfilePic.show(URL(string: "data:image/jpeg;base64,")!), gradient: ["#aa44ff", "#2349ad"])
		}.preferredColorScheme(.dark)
    }
}
